import React, {Component} from 'react';
import { Column, Row } from 'simple-flexbox';
import { StyleSheet, css } from 'aphrodite';
import './App.css';
import Sidebar from './Content/SIdebar/SideBar';
import HeaderComponent from './Content/Header/HeaderComponent'
import Content from './Content/MainPage/Content'
import Login from './Content/Login/Login/Login'

const styles = StyleSheet.create({
  container: {
      height: '100%',
      minHeight: '100vh'
  },
  content: {
      marginTop: 54
  },
  mainBlock: {
      backgroundColor: '#F7F8FC',
      padding: 30,
      paddingBottom: '30px'
  },
});

class App extends Component{
  state = { selectedItem: 'Dashboard',
            isSignedin: false       
    };

    componentDidMount() {
        window.addEventListener('resize', this.resize);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.resize);
    }

    resize = () => this.forceUpdate();
  render(){
    const { selectedItem, isSignedin } = this.state;
    if (isSignedin === false) {
      return(
        <Login/>
      )
    } else if (isSignedin == true) {
      return (
        <div>
          <Row className={css(styles.container)}>
                    <Sidebar selectedItem={selectedItem} onChange={(selectedItem) => this.setState({ selectedItem })} />
                    <Column flexGrow={1} className={css(styles.mainBlock)}>
                        <HeaderComponent title={selectedItem} onChange={(selectedItem) => this.setState({ selectedItem })}/>
                       <Content ClassName={css(styles.content)} pathname={selectedItem}/>
                    </Column>
                </Row>
        </div>
        );
    }
  }
}

export default App;
