import React, {Component} from 'react';
import { Column, Row } from 'simple-flexbox';
import { StyleSheet, css } from 'aphrodite';
import './App.css';
import Sidebar from './Content/SIdebar/SideBar';
import HeaderComponent from './Content/Header/HeaderComponent'
import Card from './Content/MainPage/Cards/Dashboard/Cards';
import SaasCard from './Content/MainPage/Cards/Sophosaas/SaasCard';
import Login from './Content/Login/Login/Login';

const styles = StyleSheet.create({
  container: {
      height: '100%',
      minHeight: '100vh'
  },
  content: {
      marginTop: 54
  },
  mainBlock: {
      backgroundColor: '#F7F8FC',
      padding: 30,
      paddingBottom: '30px'
  },
});

class App extends Component{
  state = { selectedItem: 'Dashboard',
            isSignedIn: false
    };



    componentDidMount() {
        window.addEventListener('resize', this.resize);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.resize);
    }

    resize = () => this.forceUpdate();
  render(){
    const { selectedItem, isSignedIn } = this.state;
    console.log(isSignedIn)
  return (
    <div>
      <Row className={css(styles.container)}>
                {
                  isSignedIn
                }
                <Sidebar selectedItem={selectedItem} onChange={(selectedItem) => this.setState({ selectedItem })} />
                <Column flexGrow={1} className={css(styles.mainBlock)}>
                    <HeaderComponent title={selectedItem} onChange={(selectedItem) => this.setState({ selectedItem })} isSignedin={isSignedIn}/>
                     {
                       selectedItem === 'Login' ? 
                       <Login ClassName={css(styles.content)} isSignedIn={isSignedIn} onChange={(isSignedIn) => this.setState({ isSignedIn })}/>
                      : selectedItem === 'Sophos as a Service' ? 
                      <SaasCard ClassName={css(styles.content)}/>
                      : selectedItem === 'Dashboard'?
                      <Card ClassName={css(styles.content)}/>
                      :<Login ClassName={css(styles.content)} />
                     }
                </Column>
            </Row>
    </div>
    );
  }
}

export default App;
